SELECT COUNT
( * ) as attr1
FROM
pms_user
WHERE
  1=1

    AND id IN (
    SELECT DISTINCT DU.USER_ID FROM tb_ums_dept_user DU WHERE DU.DEPT_ID IN

        #{item.deptId}

    )


    AND administrative_level in

        #{item}



    AND technical_position in

        #{item}



    AND to_there_time  >=#{start_time}


    AND to_there_time  <=#{end_time}

AND (trash_flag != '1'  or trash_flag is null)
and  user_name NOT IN ('环科院管理员','超级管理员')

    and  birth >=#{startWorkTime}



    AND birth <=#{endWorkTime}

select count(*)as attr1 from pms_user pp left join ( SELECT * FROM (SELECT t2.id,string_agg(whole_name,',')deptNames FROM tb_ums_dept_user t1 LEFT JOIN pms_user t2 ON t1.user_id = t2.id LEFT JOIN tb_ums_dept t3 ON t3.id = t1.dept_id group by t2.id)O ) ppo on ppo.id = pp.id where (pp.trash_flag != '1' or pp.trash_flag is null) and pp.user_name NOT IN ('环科院管理员','超级管理员') and pp.research_field =#{attr2}
AND ppo.deptNames LIKE '%${attr10}%'
AND pp.administrative_level IN
#{zcItem}
AND pp.technical_position IN
#{zwItem}
AND pp.person_type IN
#{personTypeItem}

