mapper.select:
SELECT

FROM pms_user O



        O.ID IN (
        SELECT DISTINCT DU.USER_ID FROM tb_ums_dept_user DU WHERE DU.DEPT_ID IN

            #{item.deptId}

        )


        AND O.ID = #{id}


        AND O.user_id = #{userId}


        AND O.user_name like '%${userName}%'


        AND O.person_type = #{personType}


        AND O.sex = #{sex}


        AND O.nation = #{nation}


        AND O.highest_education = #{highestEducation}


        AND O.highest_degree = #{highestDegree}


        AND O.technical_position = #{technicalPosition}


        AND O.administrative_level IN (${administrativeLevel})


        AND O.first_education = #{firstEducation}


        AND O.first_degree = #{firstDegree}


        AND O.job_education = #{jobEducation}


        AND O.job_degree = #{jobDegree}


        AND O.temp_work_experience = #{tempWorkExperience}


        AND O.administrative_duty LIKE '%${administrativeDuty}%'



            AND O.trash_flag = #{trashFlag}


            AND (O.trash_flag != '1' OR O.trash_flag IS NULL)





        ORDER BY O.${orderBy} ${orderType}


        ORDER BY O.num ASC,O.id ASC

SELECT

FROM pms_user O
LEFT JOIN tb_ums_user tuu ON tuu.account = O.user_id
LEFT JOIN pms_education pe ON pe.user_id = O.id
LEFT JOIN pms_work pw ON pw.user_id = O.id
LEFT JOIN pms_communication pc ON pc.id = O.id



        O.ID IN (
        SELECT DISTINCT DU.USER_ID FROM tb_ums_dept_user DU WHERE DU.DEPT_ID IN

            #{item.deptId}

        )


        AND O.ID = #{id}


        AND O.user_id = #{userId}


        AND O.user_name like '%${userName}%'


        AND O.person_type = #{personType}


        AND O.sex = #{sex}


        AND O.nation = #{nation}


        AND O.highest_education = #{highestEducation}


        AND O.highest_degree = #{highestDegree}


        AND O.technical_position = #{technicalPosition}


        AND O.administrative_level IN (${administrativeLevel})


        AND O.first_education = #{firstEducation}


        AND O.first_degree = #{firstDegree}


        AND O.job_education = #{jobEducation}


        AND O.job_degree = #{jobDegree}


        AND O.temp_work_experience = #{tempWorkExperience}


        AND O.administrative_duty LIKE '%${administrativeDuty}%'





        AND pe.start_time >= #{startEducation}


        AND pe.stop_time <= #{endEducation}


        AND pe.content LIKE  '%${educationContent}%'





        AND pw.start_time >= #{work_StartTime}


        AND pw.stop_time <= #{workStopTime}


        AND pw.content LIKE  '%${workContent}%'





        AND pc.office_phone = #{officePhone}


        AND pc.mobile_phone = #{mobilePhone}


        AND pc.email_address = #{emailAddress}








            AND O.trash_flag = #{trashFlag}






        ORDER BY O.${orderBy} ${orderType}


        ORDER BY O.num ASC,O.id ASC

SELECT

FROM pms_user O


    O.ID IN (
    SELECT DISTINCT DU.USER_ID FROM tb_ums_dept_user DU WHERE DU.DEPT_ID IN

        #{item.deptId}

    )

        AND O.ID = #{id}


        AND O.user_id = #{userId}


        AND O.user_name like '%${userName}%'


        AND O.person_type = #{personType}


        AND O.sex = #{sex}


        AND O.nation = #{nation}


        AND O.highest_education = #{highestEducation}


        AND O.highest_degree = #{highestDegree}


        AND O.technical_position = #{technicalPosition}


        AND O.administrative_level IN (${administrativeLevel})


        AND O.first_education = #{firstEducation}


        AND O.first_degree = #{firstDegree}


        AND O.job_education = #{jobEducation}


        AND O.job_degree = #{jobDegree}


        AND O.administrative_duty LIKE '%${administrativeDuty}%'



            AND O.trash_flag = #{trashFlag}


            AND (O.trash_flag != '1' OR O.trash_flag IS NULL)





        ORDER BY O.${orderBy} ${orderType}


        ORDER BY O.num ASC,O.id ASC

SELECT

FROM pms_user O

WHERE O.ID=#{id}

SELECT

FROM pms_user O

WHERE O.user_id=#{account}

