mapper.insert:
INSERT INTO pms_website
(id, user_id, chinese_name, website, add_time, sort)
VALUES
    (
        #{id}, #{userId}, #{chinese_name}, #{website}, #{add_time}, #{sort}
    )

mapper.update:
UPDATE pms_website
SET user_id = #{userId}, chinese_name = #{chinese_name}, website = #{website}, add_time = #{add_time},
    sort = #{sort}

WHERE id = #{id}
